mapper.select:
SELECT id, name, email, create_date, update_date
FROM users
WHERE id = #{id}

SELECT id, name, email, create_date, update_date
FROM users
WHERE name LIKE #{name}


    AND status = #{status}


AND active_flag = 'Y'  
ORDER BY name

mapper.update:
UPDATE users
SET name = #{name},
    email = #{email},
    update_date = #{updateDate}
WHERE id = #{id}


    AND status = #{status}


AND EXISTS (SELECT 1 FROM user_profiles WHERE user_id = users.id)
